                          sepc_rd_data |
                          scause_rd_data |
                          stval_rd_data | 
                          scounteren_rd_data |
                          sscratch_rd_data |
                          satp_rd_data |
                          utvec_rd_data |
                          uepc_rd_data |
                          ucause_rd_data |
                          utval_rd_data | 
                          uscratch_rd_data |
                          ucycle_rd_data |
                          ucycleh_rd_data |
                          mcycle_rd_data |
                          mcycleh_rd_data |
                          utime_rd_data |
                          utimeh_rd_data |
