module miv_rv32_gpr_ram_mux
    //******************************************************************************
    // Parameter description
    #(
    parameter ECC_ENABLE = 1 
    )
    //******************************************************************************
    // Port Description
    (
    input clk,
    input resetn,
    input mux_sel,
    
    input [5:0]  addr_0,
    input [31:0] data_0,
    input write_en_0,
    
    input [5:0]  addr_1,
    input [31:0] data_1,
    input write_en_1, 
   
    output reg [5:0] addr_out,
    output reg [31:0] data_out,
    output reg write_en_out
    );
    //******************************************************************************
    // Main code
    
    always @ (*)
        begin
            case(mux_sel)
            1'b0: begin
                  addr_out <= addr_0;
                  data_out <= data_0;
                  write_en_out <= write_en_0;
                  end
            1'b1: begin
                  addr_out <= addr_1;
                  data_out <= data_1;
                  write_en_out <= write_en_1;
                  end
            
         default: begin
                  addr_out <= 6'b0;
                  data_out <= 32'b0;
                  write_en_out <= 1'b0;
                  end
            endcase
        end
endmodule
