      //.bceb(ram_bceb),
      //.bweb(ram_bweb),
      //.bwemb(ram_bwemb),
      //.bdin(ram_bdin),
      //.bdout(ram_bdout),
      //.low_power(low_power)
    //);


  miv_rv32_ram_dport_reg
    #(
      .d_width(BISTW_WIDTH),
      .addr_width(ADDR_WIDTH-2),
      .mem_depth(RAM_DEPTH)
    )
    ram_inst
    (
      .clk(aclk),
      .waddr0(ram_aaddr[ADDR_WIDTH-1:2]),
      .we0(ram_aweb),
      .data0(ram_adin),
      .q0(ram_adout),
      .waddr1(ram_baddr[ADDR_WIDTH-1:2]),
      .we1(ram_bweb),
      .data1(ram_bdin),
      .q1(ram_bdout)
    );



 // BIST wrapper
  miv_rv32_bist_template_dual_behav
    #(
      .RAM_DEPTH(RAM_DEPTH),
      .DATA_WIDTH(BISTW_WIDTH),
      .ADDR_WIDTH(ADDR_WIDTH),
      .LOW_POWER_WIDTH(LOW_POWER_WIDTH),
